import {useGame} from '../contexts/GameContext';

export default function WaitingRoom() {
    const {gameState, isHost, startGame, leaveGame, gameId, playerName} = useGame();

    const handleStartGame = () => {
        if (gameState?.players?.length >= gameState?.settings?.minPlayers) {
            startGame();
        }
    };

    const canStart = isHost && gameState?.players?.length >= (gameState?.settings?.minPlayers || 4);

    return (
        <div className="waiting-room">
            <div className="waiting-container">
                <div className="game-header">
                    <h2> Game: {gameId}</h2>
                    <button onClick={leaveGame} className="leave-btn">
                        Leave Game
                    </button>
                </div>

                <div className="player-info">
                    <h3>You are: <span className="player-name">{playerName}</span></h3>
                    {isHost && <span className="host-badge"> Host</span>}
                </div>

                <div className="players-section">
                    <h3>
                        Players ({gameState?.players?.length || 0}/{gameState?.settings?.maxPlayers || 10})
                    </h3>

                    <div className="players-list">
                        {gameState?.players?.map((player, index) => (
                            <div key={player.socketId} className="player-item">
                                <span className="player-number">{index + 1}</span>
                                <span className="player-name">{player.name}</span>
                                {player.isHost && <span className="host-icon"></span>}
                            </div>
                        ))}
                    </div>
                </div>

                <div className="game-settings">
                    <h4>Game Settings:</h4>
                    <div className="settings-grid">
                        <div>Min Players: {gameState?.settings?.minPlayers || 4}</div>
                        <div>Max Players: {gameState?.settings?.maxPlayers || 10}</div>
                    </div>
                </div>

                {isHost && (
                    <div className="host-controls">
                        <button
                            onClick={handleStartGame}
                            disabled={!canStart}
                            className={`start-btn ${canStart ? 'ready' : 'disabled'}`}
                        >
                            {canStart ? ' Start Game' : `Need ${(gameState?.settings?.minPlayers || 4) - (gameState?.players?.length || 0)} more players`}
                        </button>
                    </div>
                )}

                <div className="waiting-message">
                    {isHost ?
                        "Waiting for you to start the game..." :
                        "Waiting for the host to start the game..."
                    }
                </div>
            </div>
        </div>
    );
}

import {useGame} from '../contexts/GameContext';
import PlayerList from './PlayerList';
import VotingPanel from './VotingPanel';
import RoleInfo from './RoleInfo';

export default function GameBoard() {
    const {gameState, role, phase} = useGame();

    if (!gameState) {
        return <div>Loading game...</div>;
    }

    return (
        <div className="game-board">
            <div className="game-header">
                <h2>Day {gameState.dayCount}</h2>
                <div className="phase-indicator">
                    Phase: {phase}
                </div>
            </div>

            <div className="game-content">
                <div className="left-panel">
                    <RoleInfo role={role}/>
                    <PlayerList players={gameState.players}/>
                </div>

                <div className="main-panel">
                    {phase === 'voting' && <VotingPanel/>}
                </div>
            </div>
        </div>
    );
}

import {useState} from 'react';
import {useGame} from '../contexts/GameContext';

export default function GameLobby() {
    const [gameId, setGameId] = useState('');
    const [playerName, setPlayerName] = useState('');
    const [mode, setMode] = useState('join'); // 'join' or 'create'

    const {createGame, joinGame, error, connected} = useGame();

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!gameId.trim() || !playerName.trim()) {
            return;
        }

        if (mode === 'create') {
            createGame(gameId.trim(), playerName.trim());
        } else {
            joinGame(gameId.trim(), playerName.trim());
        }
    };

    const generateGameId = () => {
        const id = Math.random().toString(36).substring(2, 8).toUpperCase();
        setGameId(id);
    };

    if (!connected) {
        return (
            <div className="lobby">
                <div className="connecting">
                    <h2> Connecting to server...</h2>
                </div>
            </div>
        );
    }

    return (
        <div className="lobby">
            <div className="lobby-container">
                <h1> Werewolf Game</h1>

                {error && (
                    <div className="error-message">
                         {error}
                    </div>
                )}

                <div className="mode-selector">
                    <button
                        className={mode === 'join' ? 'active' : ''}
                        onClick={() => setMode('join')}
                    >
                        Join Game
                    </button>
                    <button
                        className={mode === 'create' ? 'active' : ''}
                        onClick={() => setMode('create')}
                    >
                        Create Game
                    </button>
                </div>

                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <label>
                            Your Name:
                            <input
                                type="text"
                                value={playerName}
                                onChange={(e) => setPlayerName(e.target.value)}
                                placeholder="Enter your name"
                                maxLength={20}
                                required
                            />
                        </label>
                    </div>

                    <div className="input-group">
                        <label>
                            Game ID:
                            <div className="game-id-input">
                                <input
                                    type="text"
                                    value={gameId}
                                    onChange={(e) => setGameId(e.target.value.toUpperCase())}
                                    placeholder="Enter game ID"
                                    maxLength={6}
                                    required
                                />
                                {mode === 'create' && (
                                    <button
                                        type="button"
                                        onClick={generateGameId}
                                        className="generate-btn"
                                    >
                                        Generate
                                    </button>
                                )}
                            </div>
                        </label>
                    </div>

                    <button type="submit" className="submit-btn">
                        {mode === 'create' ? ' Create Game' : ' Join Game'}
                    </button>
                </form>

                <div className="game-info">
                    <h3>How to Play:</h3>
                    <ul>
                        <li> Werewolves try to eliminate villagers at night</li>
                        <li> Villagers vote to eliminate suspected werewolves during the day</li>
                        <li> Special roles have unique abilities</li>
                        <li> Win by eliminating the opposing team</li>
                    </ul>
                </div>
            </div>
        </div>
    );
}

