{
  "name": "werewolf-game",
  "scripts": {
    "dev": "concurrently \"npm run server\" \"npm run client\"",
    "server": "cd server && npm run dev",
    "client": "cd client && npm run dev",
    "build": "cd client && npm run build",
    "start": "cd server && npm start"
  },
  "devDependencies": {
    "concurrently": "^7.6.0"
  }
}

const mongoose = require('mongoose');

const connectDB = async () => {
    try {
        // Simple connection for development with Docker
        const mongoURI = process.env.MONGODB_URI || 'mongodb://admin:password123@localhost:27017/werewolf-game?authSource=admin';

        await mongoose.connect(mongoURI);
        console.log(' MongoDB connected successfully');
    } catch (error) {
        console.error(' MongoDB connection error:', error);
        console.error('Make sure Docker MongoDB is running: docker-compose up -d');
        process.exit(1);
    }
};

module.exports = connectDB;

