const Game = require('./models/Game');

class GameLogic {
    static ROLES = {
        WEREWOLF: 'werewolf',
        VILLAGER: 'villager',
        SEER: 'seer',
        DOCTOR: 'doctor',
        MAYOR: 'mayor'
    };

    static assignRoles(players) {
        const playerCount = players.length;
        const roles = [];

        // Basic role distribution
        if (playerCount >= 4) {
            roles.push(this.ROLES.WEREWOLF);
            roles.push(this.ROLES.SEER);

            if (playerCount >= 6) {
                roles.push(this.ROLES.DOCTOR);
            }

            if (playerCount >= 8) {
                roles.push(this.ROLES.WEREWOLF); // Second werewolf
            }

            if (playerCount >= 10) {
                roles.push(this.ROLES.MAYOR);
            }
        }

        // Fill remaining slots with villagers
        while (roles.length < playerCount) {
            roles.push(this.ROLES.VILLAGER);
        }

        // Shuffle roles
        for (let i = roles.length - 1; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [roles[i], roles[j]] = [roles[j], roles[i]];
        }

        return roles;
    }

    static checkWinCondition(game) {
        const alivePlayers = game.players.filter(p => p.isAlive);
        const aliveWerewolves = alivePlayers.filter(p => p.role === this.ROLES.WEREWOLF);
        const aliveVillagers = alivePlayers.filter(p => p.role !== this.ROLES.WEREWOLF);

        if (aliveWerewolves.length === 0) {
            return 'villagers';
        }

        if (aliveWerewolves.length >= aliveVillagers.length) {
            return 'werewolves';
        }

        return null;
    }

    static calculateVotes(players) {
        const voteCounts = {};
        players.forEach(player => {
            if (player.votedFor) {
                voteCounts[player.votedFor] = (voteCounts[player.votedFor] || 0) + 1;
            }
        });

        let maxVotes = 0;
        let eliminatedPlayer = null;

        Object.entries(voteCounts).forEach(([playerId, votes]) => {
            if (votes > maxVotes) {
                maxVotes = votes;
                eliminatedPlayer = playerId;
            }
        });

        return {eliminatedPlayer, voteCounts};
    }
}

module.exports = GameLogic;

const express = require('express');
const http = require('http');
const {Server} = require('socket.io');
const cors = require('cors');
const connectDB = require('./config/database');
const GameManager = require('./managers/GameManager');

// Connect to database
connectDB();

const app = express();
app.use(cors());
app.use(express.json());

const server = http.createServer(app);
const io = new Server(server, {
    cors: {
        origin: "http://localhost:5173",
        methods: ["GET", "POST"],
        credentials: true
    },
    pingTimeout: 60000,
    pingInterval: 25000
});

const gameManager = new GameManager(io);

// REST API Routes
app.get('/', (req, res) => {
    res.send(' Werewolf Game Server is running!');
});

app.post('/api/games', async (req, res) => {
    try {
        const {gameId, hostName} = req.body;
        // Game creation logic here
        res.json({success: true, gameId});
    } catch (error) {
        res.status(400).json({error: error.message});
    }
});

// Socket.io Event Handlers
io.on('connection', (socket) => {
    console.log(' A user connected:', socket.id);

    socket.emit('welcome', {message: 'Welcome to Werewolf Game!'});

    socket.on('create-game', async (data) => {
        try {
            const {gameId, playerName} = data;
            const game = await gameManager.createGame(gameId, socket.id, playerName);

            socket.join(gameId);
            socket.emit('game-created', {game});

            console.log(` Game created: ${gameId} by ${playerName}`);
        } catch (error) {
            socket.emit('error', {message: error.message});
        }
    });

    socket.on('join-game', async (data) => {
        try {
            const {gameId, playerName} = data;
            const game = await gameManager.joinGame(gameId, socket.id, playerName);

            socket.join(gameId);
            io.to(gameId).emit('player-joined', {game});

            console.log(` ${playerName} joined game: ${gameId}`);
        } catch (error) {
            socket.emit('error', {message: error.message});
        }
    });

    socket.on('start-game', async (data) => {
        try {
            const {gameId} = data;
            const game = await gameManager.startGame(gameId);

            // Send role information privately to each player
            game.players.forEach(player => {
                io.to(player.socketId).emit('role-assigned', {
                    role: player.role,
                    gameState: {
                        phase: game.phase,
                        dayCount: game.dayCount,
                        players: game.players.map(p => ({
                            name: p.name,
                            isAlive: p.isAlive,
                            socketId: p.socketId
                        }))
                    }
                });
            });

            io.to(gameId).emit('game-started', {game});
            console.log(` Game started: ${gameId}`);
        } catch (error) {
            socket.emit('error', {message: error.message});
        }
    });

    socket.on('vote', async (data) => {
        try {
            const {gameId, targetSocketId} = data;
            const game = await gameManager.processVote(gameId, socket.id, targetSocketId);

            io.to(gameId).emit('vote-cast', {game});

            if (game.phase === 'ended') {
                io.to(gameId).emit('game-ended', {game});
            }
        } catch (error) {
            socket.emit('error', {message: error.message});
        }
    });

    socket.on('disconnect', () => {
        console.log(' User disconnected:', socket.id);
        // Handle player disconnection
    });

    socket.on('error', (err) => {
        console.error('Socket error:', err);
    });
});

const PORT = process.env.PORT || 3001;
server.listen(PORT, () => {
    console.log(` Server running on http://localhost:${PORT}`);
});

